#!/usr/bin/env python3

def solve(str):
    ans1 = 0
    ans2 = None
    pos = 0
    for c in str:
        if c == '(':
            ans1 += 1
            pos += 1
        if c == ')':
            pos += 1
            ans1 -= 1
            if ans1 < 0 and not ans2:
                ans2 = pos

    print(f'{ans1} {ans2}')

